/* eslint-disable jsx-a11y/alt-text */
import { Fragment } from "react";
import { useParams } from "react-router-dom";
import Slider from "../components/ui/slider/Slider";
import Tags from "../components/ui/tags/Tags";
import CollapseDetails from "../components/ui/collapseDetails/CollapseDetails";
import CollapseEquipment from "../components/ui/collapseEquipment/CollapseEquipment";
import StarsList from "../components/ui/stars/StarsList";
import dataJson from "../assets/data/data";
import "./CardDetail.scss";

const CardDetails = (props) => {
  const params = useParams();
  const { id } = params;
  console.log(id, typeof id);

  const arrObj = dataJson.find((item) => item.id === id);

  // const arrObj = {
  //   id: "c67ab8a7",
  //   title: "Appartement cosy",
  //   cover:
  //     "https://s3-eu-west-1.amazonaws.com/course.oc-static.com/projects/front-end-kasa-project/accommodation-20-1.jpg",
  //   pictures: [
  //     "https://s3-eu-west-1.amazonaws.com/course.oc-static.com/projects/front-end-kasa-project/accommodation-20-1.jpg",
  //     "https://s3-eu-west-1.amazonaws.com/course.oc-static.com/projects/front-end-kasa-project/accommodation-20-2.jpg",
  //     "https://s3-eu-west-1.amazonaws.com/course.oc-static.com/projects/front-end-kasa-project/accommodation-20-3.jpg",
  //     "https://s3-eu-west-1.amazonaws.com/course.oc-static.com/projects/front-end-kasa-project/accommodation-20-4.jpg",
  //     "https://s3-eu-west-1.amazonaws.com/course.oc-static.com/projects/front-end-kasa-project/accommodation-20-5.jpg",
  //   ],
  //   description:
  //     "Votre maison loin de chez vous. Que vous veniez de l'autre bout du monde, ou juste de quelques stations de RER, vous vous sentirez chez vous dans notre appartement.",
  //   host: {
  //     name: "Nathalie Jean",
  //     picture:
  //       "https://s3-eu-west-1.amazonaws.com/course.oc-static.com/projects/front-end-kasa-project/profile-picture-12.jpg",
  //   },
  //   rating: "3",
  //   location: "Ile de France - Paris 17e",
  //   equipments: [
  //     "Équipements de base",
  //     "Micro-Ondes",
  //     "Douche italienne",
  //     "Frigo",
  //     "WIFI",
  //   ],
  //   tags: ["Batignolle", "Montmartre"],
  // };

  return (
    <Fragment>
      <Slider images={arrObj.pictures} />
      <div className="info-user">
        <div className="bio">
          <h1>{arrObj.title}</h1>
          <p>{arrObj.location}</p>
        </div>
        <div className="tags-box">
          {arrObj.tags.map((item, index) => (
            <Tags key={index} value={item} />
          ))}
        </div>
      </div>
      <div className="user-rating">
        <div className="image">
          <div className="name-details">
            <p>{arrObj.host.name}</p>
          </div>
          <div className="image-details">
            <img src={arrObj.host.picture} />
          </div>
        </div>
        <div className="stars">
          <ul>
            <StarsList value={arrObj.rating} />
          </ul>
        </div>
      </div>
      <div className="description">
        <div className="description-1">
          <CollapseDetails title="Description" text={arrObj.description} />
        </div>
        <div className="description-2">
          <CollapseEquipment title="Équipements" list={arrObj.equipments} />
        </div>
      </div>
    </Fragment>
  );
};

export default CardDetails;
