import { useState } from "react";
import ChevronRight from "../../../assets/img/chevronRight.png";
import ChevronLeft from "../../../assets/img/chevronLeft.png";

import "./Slider.scss";

const Slider = (props) => {
  const imagesArr = props.images;
  const [background, setBackground] = useState(imagesArr[0]);
  let marker = 0;

  const handleLeftClick = () => {
    // Suivant
    marker++;
    // if (marker >= imagesArr.length) {
    //   marker = 0;
    // }
    console.log(marker);

    setBackground(imagesArr[marker]);
  };

  const handleRightClick = () => {
    // Précedent
    if (marker <= 0) {
      marker = imagesArr.length;
    }
    marker--;
    setBackground(imagesArr[marker]);
  };
  console.log(imagesArr.length);

  return (
    <div className="slider" style={{ backgroundImage: `url(${background})` }}>
      <img
        src={ChevronRight}
        alt="chevron-right"
        className="chevronRight"
        onClick={handleRightClick}
      />
      <img
        src={ChevronLeft}
        alt="chevron-left"
        className="chevronLeft"
        onClick={handleLeftClick}
      />
    </div>
  );
};

export default Slider;
