import { useState } from "react";
import ChevronRight from "../../../assets/img/chevronRight.png";
import ChevronLeft from "../../../assets/img/chevronLeft.png";

import "./Slider.scss";

const Slider = (props) => {
  const [background, setBackground] = useState("");
  const imagesArr = props.images;

  return (
    <div className="slider" style={{ backgroundImage: background }}>
      <img src={ChevronRight} alt="chevron-right" className="chevronRight" />
      <img src={ChevronLeft} alt="chevron-left" className="chevronLeft" />
    </div>
  );
};

export default Slider;
