import { Fragment, useEffect, useState } from "react";
import Card from "../components/ui/card/Card";
import Banner from "../components/ui/banner/Banner";
import BackgroundBanner from "../assets/img/background.png";
import "./AllCards.scss";
import dataJson from "../assets/data/data";

const AllCards = () => {
  // const [logement, setLogement] = useState([]);

  useEffect(() => {
    async function fetchData() {
      try {
        const request = await fetch("../assets/data/data");
        const response = await request.json();
      } catch (err) {
        console.log(err.message);
      }
    }
    fetchData();
  }, []);

  const arr = [
    { id: 1, text: "Titre de la publication" },
    { id: 2, text: "Titre de la publication" },
    { id: 3, text: "Titre de la publication" },
    { id: 4, text: "Titre de la publication" },
    { id: 5, text: "Titre de la publication" },
    { id: 6, text: "Titre de la publication" },
    { id: 7, text: "Titre de la publication" },
    { id: 8, text: "Titre de la publication" },
  ];

  const arrObj = {
    id: "c67ab8a7",
    title: "Appartement cosy",
    cover:
      "https://s3-eu-west-1.amazonaws.com/course.oc-static.com/projects/front-end-kasa-project/accommodation-20-1.jpg",
    pictures: [
      "https://s3-eu-west-1.amazonaws.com/course.oc-static.com/projects/front-end-kasa-project/accommodation-20-1.jpg",
      "https://s3-eu-west-1.amazonaws.com/course.oc-static.com/projects/front-end-kasa-project/accommodation-20-2.jpg",
      "https://s3-eu-west-1.amazonaws.com/course.oc-static.com/projects/front-end-kasa-project/accommodation-20-3.jpg",
      "https://s3-eu-west-1.amazonaws.com/course.oc-static.com/projects/front-end-kasa-project/accommodation-20-4.jpg",
      "https://s3-eu-west-1.amazonaws.com/course.oc-static.com/projects/front-end-kasa-project/accommodation-20-5.jpg",
    ],
    description:
      "Votre maison loin de chez vous. Que vous veniez de l'autre bout du monde, ou juste de quelques stations de RER, vous vous sentirez chez vous dans notre appartement.",
    host: {
      name: "Nathalie Jean",
      picture:
        "https://s3-eu-west-1.amazonaws.com/course.oc-static.com/projects/front-end-kasa-project/profile-picture-12.jpg",
    },
    rating: "5",
    location: "Ile de France - Paris 17e",
    equipments: [
      "Équipements de base",
      "Micro-Ondes",
      "Douche italienne",
      "Frigo",
      "WIFI",
    ],
    tags: ["Batignolle", "Montmartre"],
  };

  return (
    <Fragment>
      <Banner
        cover={BackgroundBanner}
        text={"Chez vous, partout et ailleurs"}
      />
      <div className="card-layout">
        {arr.map((item) => (
          <Card key={item.id} data={item.text} image={arrObj.cover} />
        ))}
      </div>
    </Fragment>
  );
};

export default AllCards;
