import { useState } from "react";
import ChevronRight from "../../../assets/img/chevronRight.png";
import ChevronLeft from "../../../assets/img/chevronLeft.png";
import "./Slider.scss";

const Slider = (props) => {
  const [marker, setMarker] = useState(0);
  const imagesArr = props.images;
  // let marker = 0;
  console.log(marker);
  console.log(imagesArr.length);

  const handleLeftClick = () => {
    // Suivant
    if (marker >= imagesArr.length - 1) {
      setMarker(0);
    } else {
      setMarker(marker + 1);
    }
  };

  const handleRightClick = () => {
    // Précedent
    if (marker <= 0) {
      setMarker(imagesArr.length);
    } else {
      setMarker(marker - 1);
    }
  };

  return (
    <div
      className="slider"
      style={{ backgroundImage: `url(${imagesArr[marker]})` }}
    >
      <img
        src={ChevronRight}
        alt="chevron-right"
        className="chevronRight"
        onClick={handleLeftClick}
      />
      <img
        src={ChevronLeft}
        alt="chevron-left"
        className="chevronLeft"
        onClick={handleRightClick}
      />
    </div>
  );
};

export default Slider;
